import React, { Component } from 'react';
import firebase from './firebase'
import 'bootstrap/dist/css/bootstrap.css';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption,
  Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
  const items = [
    {
      src: 'https://www.img.live/images/2019/04/22/post1efd74.jpg',
      altText: 'Slide 1',
      caption: 'Slide 1'
    },
    {
      src: 'https://www.img.live/images/2019/04/22/post2.jpg',
      altText: 'Slide 2',
      caption: 'Slide 2'
    },
  ];
class App extends Component {

  constructor(){
     super();
     this.state = {
        orders:[],
        user_id:'',
        item_name:'',
        name:'',
        address:'',
        shown: true,
        lip:false,
        eye:false,
        cus:false,
        home:true,
        order:false,
        modal: false,
        modal1: false,
        modal2: false,
        modal3: false,
        modal4: false,
        modal5: false,
        modal6: false,
        modal7: false,
        modal8: false,
        modal9: false,
        modal10: false,
        modal11: false,
        modal12: false,
        modal13: false,
        modal14: false,
        modal15: false,
        modal16: false,
        modal17: false,
        modal18: false,
        moda119: false,
        modal20: false,
        modal21: false,
        modal22: false,
        modal23: false,
        modal24: false,
        activeIndex:0
     }
     this.handleChange = this.handleChange.bind(this)
     this.handleUpdate = this.handleUpdate.bind(this)
     this.handleSubmit = this.handleSubmit.bind(this)
     this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
    this.goToIndex = this.goToIndex.bind(this);
    this.onExiting = this.onExiting.bind(this);
    this.onExited = this.onExited.bind(this);
    this.toggle1 = this.toggle1.bind(this);
    this.toggle2 = this.toggle2.bind(this);
    this.toggle3 = this.toggle3.bind(this);
    this.toggle4 = this.toggle4.bind(this);
    this.toggle5 = this.toggle5.bind(this);
    this.toggle6 = this.toggle6.bind(this);
    this.toggle7 = this.toggle7.bind(this);
    this.toggle8 = this.toggle8.bind(this);
    this.toggle9 = this.toggle9.bind(this);
    this.toggle10 = this.toggle10.bind(this);
    this.toggle11 = this.toggle11.bind(this);
    this.toggle12 = this.toggle12.bind(this);
    this.toggle13 = this.toggle13.bind(this);
    this.toggle14 = this.toggle14.bind(this);
    this.toggle15 = this.toggle15.bind(this);
    this.toggle16 = this.toggle16.bind(this);
    this.toggle17 = this.toggle17.bind(this);
    this.toggle18 = this.toggle18.bind(this);
    this.toggle19 = this.toggle19.bind(this);
    this.toggle20 = this.toggle20.bind(this);
    this.toggle21 = this.toggle21.bind(this);
    this.toggle22 = this.toggle22.bind(this);
    this.toggle23 = this.toggle23.bind(this);
    this.toggle24 = this.toggle24.bind(this);
  }
  toggle1(param) {/*
    this.setState(prevState => ({
      modal1: !prevState.modal1
    }));
    this.head='item1'
    this.infor='description1'*/
    alert(param)
  }
  toggle2() {
    this.setState(prevState => ({
      modal1: !prevState.modal1
    }));
    this.head='item2'
    this.infor='description2'
  }
  toggle3() {
    this.setState(prevState => ({
      modal3: !prevState.modal2
    }));
    this.head=''
    this.infor=''
  }
  toggle4() {
    this.setState(prevState => ({
      modal4: !prevState.modal2
    }));
    this.head=''
    this.infor=''
  }
  toggle5() {
    this.setState(prevState => ({
      modal5: !prevState.modal2
    }));
    this.head=''
    this.infor=''
  }
  toggle6() {
    this.setState(prevState => ({
      modal6: !prevState.modal2
    }));
    this.head=''
    this.infor=''
  }
  toggle7() {
    this.setState(prevState => ({
      modal7: !prevState.modal2
    }));
  }
  toggle8() {
    this.setState(prevState => ({
      modal8: !prevState.modal2
    }));
  }
  toggle9() {
    this.setState(prevState => ({
      modal9: !prevState.modal2
    }));
  }
  toggle10() {
    this.setState(prevState => ({
      modal10: !prevState.modal2
    }));
  }
  toggle11() {
    this.setState(prevState => ({
      modal11: !prevState.modal2
    }));
  }
  toggle12() {
    this.setState(prevState => ({
      modal12: !prevState.modal2
    }));
  }
  toggle13() {
    this.setState(prevState => ({
      modal13: !prevState.modal2
    }));
  }
  toggle14() {
    this.setState(prevState => ({
      modal14: !prevState.modal2
    }));
  }
  toggle15() {
    this.setState(prevState => ({
      modal15: !prevState.modal2
    }));
  }
  toggle16() {
    this.setState(prevState => ({
      modal16: !prevState.modal2
    }));
  }
  toggle17() {
    this.setState(prevState => ({
      modal17: !prevState.modal2
    }));
  }
  toggle18() {
    this.setState(prevState => ({
      modal18: !prevState.modal2
    }));
  }
  toggle19() {
    this.setState(prevState => ({
      modal19: !prevState.modal2
    }));
  }
  toggle20() {
    this.setState(prevState => ({
      modal20: !prevState.modal2
    }));
  }
  toggle21() {
    this.setState(prevState => ({
      modal21: !prevState.modal2
    }));
  }
  toggle22() {
    this.setState(prevState => ({
      modal22: !prevState.modal2
    }));
  }
  toggle23() {
    this.setState(prevState => ({
      modal23: !prevState.modal2
    }));
  }
  toggle24() {
    this.setState(prevState => ({
      modal24: !prevState.modal2
    }));
  }
  onExiting() {
    this.animating = true;
  }

  onExited() {
    this.animating = false;
  }

  next() {
    if (this.animating) return;
    const nextIndex = this.state.activeIndex === items.length - 1 ? 0 : this.state.activeIndex + 1;
    this.setState({ activeIndex: nextIndex });
  }

  previous() {
    if (this.animating) return;
    const nextIndex = this.state.activeIndex === 0 ? items.length - 1 : this.state.activeIndex - 1;
    this.setState({ activeIndex: nextIndex });
  }

  goToIndex(newIndex) {
    if (this.animating) return;
    this.setState({ activeIndex: newIndex });
  }

  componentDidMount(){
    const ordersRef = firebase.database().ref('orders');
    ordersRef.on('value',(snapshot) => {
        let orders = snapshot.val();
        let newState = [];
        for(let item in orders){
          newState.push({
              user_id:item,
              item_name:orders[item].item_name,
              name:orders[item].name,
              address:orders[item].address
          })
        }
        this.setState({
          orders:newState
        })
    })
  }

  handleChange(e){
    this.setState({
      [e.target.name]: e.target.value
    })
  }


  handleSubmit(e){
    e.preventDefault();
    if(this.state.item_name==''){
      alert("Please choose item before orders.");
    }
    else if(this.state.name==''){
      alert("Please input Your Name.");
    }
    else if(this.state.address==''){
      alert("Please input Your Address.");
    }
    else{
    if(this.state.user_id != ''){
      return this.updateItem();
    }
    const ordersRef = firebase.database().ref('orders')
    const item = {
       item_name : this.state.item_name,
       name : this.state.name,
       address : this.state.address
    }
    ordersRef.push(item)
    this.setState({
       user_id:'',
       item_name:'',
       name:'',
       address:''
    })
  }
 }

  handleUpdate = (user_id = null , item_name = null , name = null , address = null) => {
    this.setState({user_id,item_name,name,address})
  }

  updateItem(){

      var obj = { item_name:this.state.item_name,name:this.state.name,address:this.state.address }

      const ordersRef = firebase.database().ref('/orders')

      ordersRef.child(this.state.user_id).update(obj);

      this.setState({
        user_id:'',
        item_name:'',
        name:'',
        address:''
      })

  }

  removeItem(itemId){
    const ordersRef = firebase.database().ref('/orders');
    ordersRef.child(itemId).remove();
 }
 toggleModal() {    
  this.setState((prev, props) => {
    const newState = !prev.modalState;

    return { modalState: newState };
  });
}
admin() {
  var password = window.prompt("Password")
  if(password=='admin'){
  this.setState({
    shown: !this.state.shown
  })}
  else{
    alert("wrong password");
  };
}
showLip(){
  this.setState({
    lip: true,
    eye: false,
    cus: false,
    home:false,
    order: true
  })
}
showEye(){
  this.setState({
    lip: false,
    eye: true,
    cus: false,
    home:false,
    order: true
  })
}
showCus(){
  this.setState({
    lip: false,
    eye: false,
    cus: true,
    home:false,
    order: true
  })
}
showHome(){
  this.setState({
    lip: false,
    eye: false,
    cus: false,
    home:true,
    order: false
  })
}
  render() {
    var shown = {
			display: this.state.shown ? "block" : "none"
		};

		var hidden = {
			display: this.state.shown ? "none" : "block"
    }

    var hidelip={
      display: this.state.lip ? "block" : "none"
    }
    var hideeye={
      display: this.state.eye ? "block" : "none"
    }
    var hidecus={
      display: this.state.cus ? "block" : "none"
    }
    var hidehome={
      display: this.state.home ? "block" : "none"
    }
    var hideorder={
      display: this.state.order ? "block" : "none"
    }
    const { activeIndex } = this.state;

    const slides = items.map((item) => {
      return (
        <CarouselItem
          onExiting={this.onExiting}
          onExited={this.onExited}
          key={item.src}
        >
          <img src={item.src} alt={item.altText} />
          <CarouselCaption captionText={item.caption} captionHeader={item.caption} />
        </CarouselItem>
      );
    });
    return (
      <div className="app">
      <Navbar color="light" light expand="md">
          <NavbarBrand onClick={this.showHome.bind(this)}>Home</NavbarBrand>
            <Nav className="ml-auto" navbar>
              <NavItem>
                <NavLink onClick={this.showLip.bind(this)}>Lipstick</NavLink>
              </NavItem>
              <NavItem>
                <NavLink onClick={this.showEye.bind(this)}>EyePalett</NavLink>
              </NavItem>
              <NavItem>
                <NavLink onClick={this.showCus.bind(this)}>Foundation</NavLink>
              </NavItem>

            </Nav>
        </Navbar>

      <div className="container" style={{marginTop:70}}>
      <Carousel style={hidehome}
        activeIndex={activeIndex}
        next={this.next}
        previous={this.previous}
      >
        <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={this.goToIndex} />
        {slides}
        <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
        <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} />
      </Carousel>
      <form style={hideorder} onSubmit={this.handleSubmit} onKeyPress={event => {
      if (event.which === 13) {
        event.preventDefault();
        }
      }}>
            <table style={ hidelip } className="table table-sm table-bordered">
                    <tr className="thead-light">
                    <th width="25%"><img src={require('./pic/1.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nGiorgio Armani Beauty'}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nGiorgio Armani Beauty'}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle1(1)}>{this.props.buttonLabel}Detail</Button></th>
                    <th width="25%"><img src={require('./pic/2.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nYves Saint Laurent Ro'}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nYves Saint Laurent Ro'}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle2}>{this.props.buttonLabel}Detail</Button></th>
                    <th width="25%"><img src={require('./pic/3.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nPat Mcgrath Labs Matt'}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nPat Mcgrath Labs Matt'}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle3}>{this.props.buttonLabel}Detail</Button></th>
                    <th width="25%"><img src={require('./pic/4.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nKat Von D Everlasting'}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nKat Von D Everlasting'}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle4}>{this.props.buttonLabel}Detail</Button></th>

                    </tr>
                    <tr className="thead-light">

                    <th width="25%"><img src={require('./pic/5.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nPat Mcgrath Labs Mini'}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nPat Mcgrath Labs Mini'}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle5}>{this.props.buttonLabel}Detail</Button></th>
                    <th width="25%"><img src={require('./pic/6.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nNars Velvet Matte Lip'}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nNars Velvet Matte Lip'}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle6}>{this.props.buttonLabel}Detail</Button></th>
                    <th width="25%"><img src={require('./pic/7.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nCharlotte Tilbury Mat'}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nCharlotte Tilbury Mat'}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle7}>{this.props.buttonLabel}Detail</Button></th>
                    <th width="25%"><img src={require('./pic/8.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nHuda Beauty Power Bul'}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nHuda Beauty Power Bul'}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle8}>{this.props.buttonLabel}Detail</Button></th>
                    </tr>
                    </table>
           <table style={ hideeye } className="table table-sm table-bordered">
                    <tr className="thead-light">
                    <th width="25%"><img src={require('./pic/9.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nHuda Beauty New Nude '}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nHuda Beauty New Nude '}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle9}>{this.props.buttonLabel}Detail</Button></th>
                    <th width="25%"><img src={require('./pic/10.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nCharlotte Tilbury Lux'}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nCharlotte Tilbury Lux'}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle10}>{this.props.buttonLabel}Detail</Button></th>
                    <th width="25%"><img src={require('./pic/11.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nHuda Beauty Obsession'}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nHuda Beauty Obsession'}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle11}>{this.props.buttonLabel}Detail</Button></th>
                    <th width="25%"><img src={require('./pic/12.jpg')} class="img-thumbnail" onClick={() => this.state.item_name=this.state.item_name+'\nToo Faced Natural Lus'}></img><Button color="primary" onClick={() => this.state.item_name=this.state.item_name+'\nToo Faced Natural Lus'}>{this.props.buttonLabel}Cart</Button><Button color="warning" onClick={this.toggle12}>{this.props.buttonLabel}Detail</Button></th>

